package labsession;

public class PostiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -7;
		if(number > 0) {
			System.out.println("is positive");
		}
		else if(number<0){
			System.out.println("is negative");
		}
		else {
			System.out.println("is zero");
		}

	}

}
